import React from "react";
import "bootstrap/dist/css/bootstrap.css";
import { Doughnut } from "react-chartjs-2";
import Axios from "axios";

const data = {
  labels: ["GET", "POST", "HEAD"],
  datasets: [
    {
      data: [],
      backgroundColor: ["#5bc0de", "#d9534f", "#5cb85c"],
    },
  ],
  legend: {
    position: "bottom",
  },
};

function addDataSet(chart, data) {
  const keys = Object.keys(data);
  for (const key of keys) {
    console.log(key);
  }
  //chart.update();
}

class DoughnutChart extends React.Component {
  state = {
    captureDomains: "",
    getRequest: 0,
    postRequest: 0,
    headRequest: 0,
    data: [0, 0, 0],
  };

  async componentDidMount() {
    const { data: captureDomains } = await Axios.get(
      "http://ec2-54-152-230-158.compute-1.amazonaws.com:7999/api/play"
    );
    this.setState({ captureDomains });
    captureDomains.forEach((element) => {
      if (element.method === "GET") ++this.state.getRequest;
      else if (element.method === "POST") ++this.state.postRequest;
      else if (element.method === "HEAD") ++this.state.headRequest;
    });
    this.setState({
      data: [
        this.state.getRequest,
        this.state.postRequest,
        this.state.headRequest,
      ],
    });
    addDataSet(Doughnut, [3, 5, 3]);
  }

  render() {
    return (
      <React.Fragment>
        <h3>
          <p class="text-muted text-center">Methods</p>
        </h3>
        <Doughnut data={data} />
      </React.Fragment>
    );
  }
}

export default DoughnutChart;
